SELECT * FROM ChangePointDetection (
  ON finance_data2 PARTITION BY sid ORDER BY id
  USING
  TargetColumn ('expenditure')
  Accumulate ('sid', 'id', 'expenditure')
) AS dt;

SELECT * FROM ChangePointDetection (
  ON cpt PARTITION BY sid ORDER BY id
  USING
  TargetColumn ('val')
  Accumulate ('sid', 'id')
) AS dt;
