CREATE MULTISET TABLE model_table_name AS (
  SELECT * FROM NaiveBayesReduce (
    ON (
      SELECT * FROM NaiveBayesMap (
        ON { table | view | (query) }
        USING
        ResponseColumn ('response_column')
        [ NumericInputs ({ 'numeric_input_column' | numeric_input_column_range }[,...] ) ]
        [ CategoricalInputs ({ 'categorical_input_column' | categorical_input_column_range }[,...] ) ]
      ) AS alias_1
    ) PARTITION BY partition_column [ ORDER BY cnt ]
  ) AS alias_2
) WITH DATA;

DROP TABLE nb_iris_input_train;
DROP TABLE nb_iris_input_test;

CREATE MULTISET TABLE nb_iris_input_train AS (
  SELECT * FROM nb_input_iris WHERE id MOD 5 <> 0
) WITH DATA;

CREATE MULTISET TABLE nb_iris_input_test AS (
  SELECT * FROM nb_input_iris WHERE id MOD 5 = 0
) WITH DATA;

CREATE MULTISET TABLE nb_iris_model AS (
  SELECT * FROM NaiveBayesReduce (
    ON NaiveBayesMap (
      ON nb_iris_input_train
      USING
      ResponseColumn ('species')
      NumericInputs ('[1:4]')
    ) PARTITION BY class_nb
  ) AS dt 
) WITH DATA;
